import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { User } from '../../services/User';
import { DataGatewayService } from '../../services/data-gateway.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  userFirstName!: string;
  userLastName!: string;
  email!: string; 
  password!: string;
  role: string = "USER";
  phoneNumber!: string;
  address!: string;
  registrationMessage:string|undefined;

  constructor(private router: Router,private dataService: DataGatewayService){}
  

  save() {

    const accountRegistration: User = {
      userFirstName: this.userFirstName,
      userLastName: this.userLastName,
      email: this.email,
      password: this.password,
      role: this.role,
      userPhoneNumber: this.phoneNumber,
      userAddress: this.address

    };

    console.log(accountRegistration);

    let resultData: any =
      this.dataService.getAccountRegistered(accountRegistration).subscribe((accountRegistration) => resultData);
    
    this.registrationMessage = "Registered Successfully";
   
    setTimeout(() => {
      this.registrationMessage = undefined;
      this.router.navigateByUrl('/login');
    }, 2000);
    //console.log(resultData);
    
  }

}
<div class="register-container">
    <div class="register-page p-4 mt-5">
        <form>
            <fieldset>
              <legend>REGISTER</legend>
              <div class="form-group">
                <label for="InputFirstName" class="form-label mt-4">First Name</label>
                <input type="text" class="form-control" id="InputFirstName" placeholder="Enter First Name">
              </div>
              <div class="form-group">
                <label for="InputLastName" class="form-label mt-4">Last Name</label>
                <input type="text" class="form-control" id="InputLastName" placeholder="Enter Last Name">
              </div>
              <div class="form-group">
                <label for="InputEmail2" class="form-label mt-4">Email address</label>
                <input type="email" class="form-control" id="InputEmail2" aria-describedby="emailHelp" placeholder="Enter email">
                <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
              </div>
              <div class="form-group">
                <label for="InputPassword2" class="form-label mt-4">Password</label>
                <input type="password" class="form-control" id="InputPassword2" placeholder="Password">
              </div>
              <div class="form-group">
                <label for="InputNumber" class="form-label mt-4">Phone Number</label>
                <input type="number" class="form-control" id="InputNumber" placeholder="Phone Number">
              </div>
              <div class="form-group">
                <label for="InputAddress" class="form-label mt-4">Address</label>
                <input type="text" class="form-control" id="InputAddress" placeholder="Address">
              </div>
              
              <button type="submit" class="btn btn-primary my-4">Register</button>
            </fieldset>
        </form>
    </div>
</div>
